changeSet.sql:
DROP TABLE IF EXISTS "user";
CREATE TABLE "user" ( id SERIAL NOT NULL, first_name VARCHAR(255) NOT NULL, last_name VARCHAR(255) NOT NULL, username VARCHAR(255) NOT NULL UNIQUE, password VARCHAR(255) NOT NULL, email VARCHAR(255) NOT NULL UNIQUE, role VARCHAR(255) NOT NULL,
CONSTRAINT PK_id PRIMARY KEY (id) );

